from allauth.account.forms import LoginFormfrom django import formsclass CustomLoginForm(LoginForm):    def __init__(self, *args, **kwargs):        super(CustomLoginForm, self).__init__(*args, **kwargs)        self.fields['login'].widget = forms.TextInput(            attrs={                'placeholder': '아이디를 입력해주세요111',                'class': 'form-control',            }        )        # self.fields['']